import React, { useState, useEffect, useRef } from 'react';
import AceEditor from 'react-ace';
import 'ace-builds/src-noconflict/mode-python';
import 'ace-builds/src-noconflict/theme-monokai';

const CodeEditorWithTabs = () => {
  const editorRef = useRef(null);

  // Maintain multiple buffers as an object, with tab names as keys
  const [buffers, setBuffers] = useState({
    Tab1: '# Write code for Tab 1',
    Tab2: '# Write code for Tab 2',
    Tab3: '# Write code for Tab 3',
  });

  // Track the currently active tab
  const [activeTab, setActiveTab] = useState('Tab1');

  // Update Ace editor content when switching tabs
  useEffect(() => {
    if (editorRef.current && editorRef.current.editor) {
      editorRef.current.editor.setValue(buffers[activeTab], -1); // -1 to move cursor to start
    }
  }, [activeTab, buffers]);

  // Handle code change for the active tab
  const handleCodeChange = (newCode) => {
    setBuffers((prevBuffers) => ({
      ...prevBuffers,
      [activeTab]: newCode,
    }));
  };

  return (
    <div>
      {/* Tab Navigation */}
      <div style={{ marginBottom: '10px' }}>
        {Object.keys(buffers).map((tab) => (
          <button
            key={tab}
            onClick={() => setActiveTab(tab)}
            style={{
              padding: '10px',
              margin: '0 5px',
              backgroundColor: activeTab === tab ? '#007BFF' : '#ccc',
              color: '#fff',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer',
            }}
          >
            {tab}
          </button>
        ))}
      </div>

      {/* Ace Editor */}
      <AceEditor
        ref={editorRef}
        mode="python"
        theme="monokai"
        name="code_editor"
        onChange={handleCodeChange}
        value={buffers[activeTab]}
        width="100%"
        height="300px"
        fontSize={14}
        setOptions={{
          showLineNumbers: true,
          tabSize: 4,
          useWorker: false,
        }}
      />
    </div>
  );
};

export default CodeEditorWithTabs;
