// App.js

import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import ButtonPanel from './components/ButtonPanel';
import CodeEditor from './components/CodeEditor';
import GraphDisplay from './components/GraphDisplay';
import { ReactFlowProvider } from 'reactflow';
import { initialNodes, initialEdges, order } from './graphElements';
import initialCodeData from './initialCode.json'; // Import the JSON file

function App() {
  const [buffers, setBuffers] = useState({
    Original: '',
    Decomposed: '',
  });
  const [activeTab, setActiveTab] = useState('Original');
  const [nodes, setNodes] = useState([]);
  const [edges, setEdges] = useState([]);
  const editorRef = useRef(null);
  const graphRef = useRef(null);

  // Load initial code from JSON and localStorage
  useEffect(() => {
    // Load code from localStorage if it exists, otherwise load from JSON
    const storedOriginal = localStorage.getItem('Original');
    const storedDecomposed = localStorage.getItem('Decomposed');

    setBuffers({
      Original: storedOriginal || initialCodeData.Original,
      Decomposed: storedDecomposed || initialCodeData.Decomposed,
    });
  }, []);

  // Save to localStorage whenever buffers change
  useEffect(() => {
    localStorage.setItem('Original', buffers.Original);
    localStorage.setItem('Decomposed', buffers.Decomposed);
  }, [buffers]);

  const handleProcessCode = () => {
    const decomposedContent = `# Decomposed code based on ${buffers.Original}`;
    setBuffers((prevBuffers) => ({
      ...prevBuffers,
      Decomposed: decomposedContent,
    }));
    setNodes(initialNodes);
    setEdges(initialEdges);
  };

  const handleRerunAnimation = () => {
    if (graphRef.current) {
      graphRef.current.resetGraphColors();
    }
  };

  return (
    <div className="app-container">
      <ButtonPanel
        onProcessCode={handleProcessCode}
        onTabChange={setActiveTab}
        activeTab={activeTab}
        onRerunAnimation={handleRerunAnimation}
      />

      <div className="content-container">
        <div className="editor-section">
          <CodeEditor
            code={buffers[activeTab]}
            setCode={(newCode) =>
              setBuffers((prevBuffers) => ({
                ...prevBuffers,
                [activeTab]: newCode,
              }))
            }
            editorRef={editorRef}
          />
        </div>

        <div className="graph-section">
          <ReactFlowProvider>
            <GraphDisplay
              ref={graphRef}
              nodes={nodes}
              edges={edges}
              order={order}
              editorRef={editorRef}
              setNodes={setNodes}
              setEdges={setEdges}
            />
          </ReactFlowProvider>
        </div>
      </div>
    </div>
  );
}

export default App;
