import React, { useEffect, useCallback, useRef, useState, forwardRef, useImperativeHandle } from 'react';
import ReactFlow, { useNodesState, useEdgesState, useReactFlow } from 'reactflow';
import 'reactflow/dist/style.css';
import { applyStyles } from '../utils/GraphUtils';
import ace from 'ace-builds';

const GraphDisplay = forwardRef(({ nodes: initialNodes, edges: initialEdges, order, editorRef, activeTab }, ref) => {
  const [nodes, setNodesState, onNodesChange] = useNodesState([]);
  const [edges, setEdgesState, onEdgesChange] = useEdgesState([]);
  const { fitView } = useReactFlow();
  const animationTimeoutRef = useRef([]);
  const colorClassesRef = useRef(new Map());
  const [animationRun, setAnimationRun] = useState(false);

  // Separate highlight lists for each view
  const lineHighlights = useRef({ Original: [], Decomposed: [] });

  const createColorClass = (color) => {
    if (!colorClassesRef.current.has(color)) {
      const className = `highlight-${color.replace('#', '')}`;
      const style = document.createElement('style');
      style.innerHTML = `
        .${className} {
          position: absolute;
          background: ${color}40;
          z-index: 20;
        }
      `;
      document.head.appendChild(style);
      colorClassesRef.current.set(color, className);
    }
    return colorClassesRef.current.get(color);
  };

  const highlightCodeLines = useCallback((lineNumbers, color, view) => {
    if (editorRef.current && editorRef.current.editor) {
      const session = editorRef.current.editor.getSession();
      const Range = ace.require('ace/range').Range;

      const colorClass = createColorClass(color);
      const markers = lineNumbers.map((line) => {
        const markerId = session.addMarker(new Range(line - 1, 0, line - 1, 1), colorClass, 'fullLine');
        return { line, markerId, colorClass };
      });

      // Save markers for the specific view
      lineHighlights.current[view] = markers;
    }
  }, [editorRef]);

  const runColorAnimation = useCallback(() => {
    if (!order || !order.length) return;

    setAnimationRun(true);
    animationTimeoutRef.current.forEach(clearTimeout);
    animationTimeoutRef.current = [];

    const view = activeTab;
    let delay = 0;

    order.forEach(([id, color]) => {
      animationTimeoutRef.current.push(
        setTimeout(() => {
          setNodesState((nds) =>
            nds.map((node) => {
              if (node.id === id) {
                const lineProperty = view === 'Original' ? 'code_lines' : 'decomp_code_lines';
                if (node[lineProperty] && node[lineProperty].length > 0) {
                  highlightCodeLines(node[lineProperty], color, view);
                }
                return {
                  ...node,
                  style: {
                    ...node.style,
                    background: color,
                  },
                };
              }
              return node;
            })
          );

          setEdgesState((eds) =>
            eds.map((edge) => {
              if (edge.id === id) {
                return {
                  ...edge,
                  animated: true,
                  style: {
                    ...edge.style,
                    stroke: color,
                    strokeWidth: 6,
                  },
                };
              }
              return edge;
            })
          );
        }, delay)
      );

      delay += 500;
    });
  }, [order, setNodesState, setEdgesState, highlightCodeLines, activeTab]);

  const clearMarkers = (view) => {
    if (editorRef.current && editorRef.current.editor) {
      const session = editorRef.current.editor.getSession();
      lineHighlights.current[view].forEach(({ markerId }) => session.removeMarker(markerId));
      lineHighlights.current[view] = []; // Clear markers for this view
    }
  };

  const applyMarkersForView = (view) => {
    if (editorRef.current && editorRef.current.editor) {
      const session = editorRef.current.editor.getSession();
      lineHighlights.current[view].forEach(({ line, colorClass }) => {
        const markerId = session.addMarker(new ace.Range(line - 1, 0, line - 1, 1), colorClass, 'fullLine');
        lineHighlights.current[view].push({ line, markerId, colorClass }); // Track applied markers
      });
    }
  };

  useImperativeHandle(ref, () => ({
    resetGraphColors() {
      animationTimeoutRef.current.forEach(clearTimeout);
      animationTimeoutRef.current = [];

      setNodesState((nds) =>
        nds.map((node) => ({
          ...node,
          style: {
            ...node.style,
            background: '#D3D3D3',
          },
        }))
      );

      setEdgesState((eds) =>
        eds.map((edge) => ({
          ...edge,
          style: {
            ...edge.style,
            stroke: '#D3D3D3',
          },
        }))
      );

      // Clear markers for both views
      clearMarkers('Original');
      clearMarkers('Decomposed');
      setAnimationRun(false); // Reset animation flag
    },
  }));

  // Reapply markers when switching tabs, if animation has run
  useEffect(() => {
    if (!animationRun) return;

    const view = activeTab;
    const otherView = activeTab === 'Original' ? 'Decomposed' : 'Original';

    clearMarkers(otherView); // Ensure other view's markers are cleared
    if (lineHighlights.current[view].length > 0) {
      applyMarkersForView(view); // Apply markers specific to the active tab
    }
  }, [activeTab, animationRun]);

  useEffect(() => {
    if (nodes.length && edges.length && !animationRun) {
      runColorAnimation();
    }
  }, [nodes, edges, runColorAnimation, animationRun]);

  useEffect(() => {
    if (initialNodes.length && initialEdges.length) {
      const { styledNodes, styledEdges } = applyStyles(initialNodes, initialEdges);
      setNodesState(styledNodes);
      setEdgesState(styledEdges);
      fitView();
    }
  }, [initialNodes, initialEdges, setNodesState, setEdgesState, fitView]);

  return (
    <div className="graph-display">
      <ReactFlow nodes={nodes} edges={edges} onNodesChange={onNodesChange} onEdgesChange={onEdgesChange} fitView />
    </div>
  );
});

export default GraphDisplay;
