import React, { useState, useEffect, useRef } from 'react';
import AceEditor from 'react-ace';
import 'ace-builds/src-noconflict/mode-python';
import 'ace-builds/src-noconflict/theme-monokai';

const CodeEditorWithTabs = () => {
  const editorRef = useRef(null);

  // State for the content of the two tabs
  const [buffers, setBuffers] = useState({
    Original: '',
    Decomposed: '',
  });

  // Track the currently active tab
  const [activeTab, setActiveTab] = useState('Original');

  // Load code content from JSON on component mount
  useEffect(() => {
    fetch('/demoCode.json')
      .then((response) => response.json())
      .then((data) => {
        setBuffers({
          Original: data.Original,
          Decomposed: '', // Start with an empty decomposed tab; filled on processing
        });
      })
      .catch((error) => console.error('Error loading demo code:', error));
  }, []);

  // Update Ace editor content when switching tabs
  useEffect(() => {
    if (editorRef.current && editorRef.current.editor) {
      editorRef.current.editor.setValue(buffers[activeTab], -1); // -1 to move cursor to start
    }
  }, [activeTab, buffers]);

  // Handle code change for the active tab
  const handleCodeChange = (newCode) => {
    setBuffers((prevBuffers) => ({
      ...prevBuffers,
      [activeTab]: newCode,
    }));
  };

  // Handle Process Code button to populate the Decomposed tab
  const handleProcessCode = () => {
    setBuffers((prevBuffers) => ({
      ...prevBuffers,
      Decomposed: buffers.Decomposed || buffers.Original, // Use Decomposed content from JSON
    }));
    setActiveTab('Decomposed'); // Automatically switch to Decomposed tab after processing
  };

  return (
    <div>
      {/* Tab Navigation */}
      <div style={{ marginBottom: '10px' }}>
        {['Original', 'Decomposed'].map((tab) => (
          <button
            key={tab}
            onClick={() => setActiveTab(tab)}
            style={{
              padding: '10px',
              margin: '0 5px',
              backgroundColor: activeTab === tab ? '#007BFF' : '#ccc',
              color: '#fff',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer',
            }}
          >
            {tab}
          </button>
        ))}
      </div>

      {/* Process Code Button */}
      <button onClick={handleProcessCode} style={{ marginBottom: '10px' }}>
        Process Code
      </button>

      {/* Ace Editor */}
      <AceEditor
        ref={editorRef}
        mode="python"
        theme="monokai"
        name="code_editor"
        onChange={handleCodeChange}
        value={buffers[activeTab]}
        width="100%"
        height="300px"
        fontSize={14}
        setOptions={{
          showLineNumbers: true,
          tabSize: 4,
          useWorker: false,
        }}
      />
    </div>
  );
};

export default CodeEditorWithTabs;
