// App.js
import React, { useState, useRef } from 'react';
import './App.css';
import CodeEditor from './components/CodeEditor';
import GraphDisplay from './components/GraphDisplay';
import { ReactFlowProvider } from 'reactflow';
import { initialNodes, initialEdges, order } from './graphElements';

function App() {
  const [buffers, setBuffers] = useState({
    Original: '# Write your Python code here\n',
    Decomposed: '',
  });
  const [activeTab, setActiveTab] = useState('Original'); // Track active tab
  const [nodes, setNodes] = useState([]);
  const [edges, setEdges] = useState([]);
  const editorRef = useRef(null);

  const handleProcessCode = () => {
    const decomposedContent = `# Decomposed code based on ${buffers.Original}`;
    setBuffers((prevBuffers) => ({
      ...prevBuffers,
      Decomposed: decomposedContent,
    }));

    // Set nodes and edges for development purposes
    setNodes(initialNodes);
    setEdges(initialEdges);
    setActiveTab('Decomposed'); // Switch to Decomposed tab after processing
  };

  return (
    <div className="app-container">
      <div className="editor-section">
        {/* Button Section */}
        <div className="button-section">
          <button className="process-button" onClick={handleProcessCode}>
            Process Code
          </button>
          {['Original', 'Decomposed'].map((tab) => (
            <button
              key={tab}
              className={`tab-button ${activeTab === tab ? 'active' : ''}`}
              onClick={() => setActiveTab(tab)}
            >
              {tab}
            </button>
          ))}
        </div>

        {/* Code Editor */}
        <CodeEditor
          code={buffers[activeTab]} // Pass active tab's content
          setCode={(newCode) =>
            setBuffers((prevBuffers) => ({
              ...prevBuffers,
              [activeTab]: newCode,
            }))
          }
          editorRef={editorRef}
        />
      </div>

      {/* Graph Section */}
      <div className="graph-section">
        <ReactFlowProvider>
          <GraphDisplay
            nodes={nodes}
            edges={edges}
            order={order}
            editorRef={editorRef} 
            setNodes={setNodes}
            setEdges={setEdges}
          />
        </ReactFlowProvider>
      </div>
    </div>
  );
}

export default App;
