// GraphDisplay.js

import React, { useEffect, useCallback, useRef, useState, forwardRef, useImperativeHandle } from 'react';
import ReactFlow, { useNodesState, useEdgesState, useReactFlow } from 'reactflow';
import 'reactflow/dist/style.css';
import { applyStyles } from '../utils/GraphUtils';
import ace from 'ace-builds';

const GraphDisplay = forwardRef(({ nodes: initialNodes, edges: initialEdges, order, editorRef, activeTab }, ref) => {
  const [nodes, setNodesState, onNodesChange] = useNodesState([]);
  const [edges, setEdgesState, onEdgesChange] = useEdgesState([]);
  const { fitView } = useReactFlow();
  const animationTimeoutRef = useRef([]);
  const [animationRun, setAnimationRun] = useState(false); // Track if animation has been run
  const originalMarkersRef = useRef([]); // Markers for Original tab
  const decomposedMarkersRef = useRef([]); // Markers for Decomposed tab
  const colorClassesRef = useRef(new Map());

  const createColorClass = (color) => {
    if (!colorClassesRef.current.has(color)) {
      const className = `highlight-${color.replace('#', '')}`;
      const style = document.createElement('style');
      style.innerHTML = `
        .${className} {
          position: absolute;
          background: ${color}40;
          z-index: 20;
        }
      `;
      document.head.appendChild(style);
      colorClassesRef.current.set(color, className);
    }
    return colorClassesRef.current.get(color);
  };

  // Highlight lines based on the activeTab using correct markers array
  const highlightCodeLines = useCallback((lineNumbers, color, isDecomposed) => {
    if (editorRef.current && editorRef.current.editor) {
      const session = editorRef.current.editor.getSession();
      const Range = ace.require('ace/range').Range;
      const colorClass = createColorClass(color);
      const markerArray = isDecomposed ? decomposedMarkersRef : originalMarkersRef;

      lineNumbers.forEach((line) => {
        const markerId = session.addMarker(new Range(line - 1, 0, line - 1, 1), colorClass, 'fullLine');
        markerArray.current.push(markerId);
      });
    }
  }, [editorRef]);

  // Clear markers for a specified view
  const clearMarkers = (isDecomposed) => {
    if (editorRef.current && editorRef.current.editor) {
      const session = editorRef.current.editor.getSession();
      const markerArray = isDecomposed ? decomposedMarkersRef : originalMarkersRef;

      markerArray.current.forEach((markerId) => session.removeMarker(markerId));
      markerArray.current = [];
    }
  };

  useEffect(() => {
    if (initialNodes.length && initialEdges.length) {
      const { styledNodes, styledEdges } = applyStyles(initialNodes, initialEdges);
      setNodesState(styledNodes);
      setEdgesState(styledEdges);
      fitView();
    }
  }, [initialNodes, initialEdges, setNodesState, setEdgesState, fitView]);

  // Run color animation and save highlights for each view
  const runColorAnimation = useCallback(() => {
    if (!order || !order.length) return;

    animationTimeoutRef.current.forEach(clearTimeout);
    animationTimeoutRef.current = [];
    setAnimationRun(true); // Mark animation as run

    let delay = 0;
    order.forEach(([id, color]) => {
      animationTimeoutRef.current.push(
        setTimeout(() => {
          setNodesState((nds) =>
            nds.map((node) => {
              if (node.id === id) {
                const lines = activeTab === 'Decomposed' ? node.decomp_code_lines : node.code_lines;
                highlightCodeLines(lines || [], color, activeTab === 'Decomposed');
                return {
                  ...node,
                  style: {
                    ...node.style,
                    background: color,
                  },
                };
              }
              return node;
            })
          );

          setEdgesState((eds) =>
            eds.map((edge) => {
              if (edge.id === id) {
                return {
                  ...edge,
                  animated: true,
                  style: {
                    ...edge.style,
                    stroke: color,
                    strokeWidth: 6,
                  },
                };
              }
              return edge;
            })
          );
        }, delay)
      );

      delay += 500;
    });
  }, [order, setNodesState, setEdgesState, highlightCodeLines, activeTab]);

  // Use useImperativeHandle to expose resetGraphColors to parent component via ref
  useImperativeHandle(ref, () => ({
    resetGraphColors() {
      animationTimeoutRef.current.forEach(clearTimeout);
      animationTimeoutRef.current = [];
      setNodesState((nds) =>
        nds.map((node) => ({
          ...node,
          style: {
            ...node.style,
            background: '#D3D3D3',
          },
        }))
      );

      setEdgesState((eds) =>
        eds.map((edge) => ({
          ...edge,
          style: {
            ...edge.style,
            stroke: '#D3D3D3',
          },
        }))
      );

      clearMarkers(true); // Clear markers for Decomposed
      clearMarkers(false); // Clear markers for Original
      setAnimationRun(false); // Reset animation run status
    },
  }));

  // Apply stored highlights when switching tabs, if animation has been run
  useEffect(() => {
    if (animationRun) {
      clearMarkers(activeTab === 'Decomposed' ? false : true); // Clear inactive tab markers
      const markersToApply = activeTab === 'Decomposed' ? decomposedMarkersRef : originalMarkersRef;
      markersToApply.current.forEach((markerId) => editorRef.current.editor.getSession().addMarker(markerId));
    }
  }, [activeTab, animationRun]);

  return (
    <div className="graph-display">
      <ReactFlow nodes={nodes} edges={edges} onNodesChange={onNodesChange} onEdgesChange={onEdgesChange} fitView />
    </div>
  );
});

export default GraphDisplay;
